#!/usr/bin/env node
var argsParser = require('../lib/birthdayArgs.js');
var args = argsParser.parseArgs();
var birthday = require('../lib/birthday.js');

var cal = birthday.generateCalendar(args);

var name = args.name;
var calculateYearsCount = args.count;
var summary = `${name}的生日`;
var fileName = `${summary}.ics`;
cal.save(`./${fileName}`, () => {
    console.log(`未来 ${calculateYearsCount} 年(包括今年)${summary}日历已保存到 - ${fileName}`);
});
