var ArgumentParser = require('argparse').ArgumentParser;

var parser = new ArgumentParser({
  version: '0.1.0',
  addHelp: true,
  description: 'Lunar Birthday Calendar(lbc) generator',
  usage: 'lbc --lunar-month=1 --lunar-day=1 --leap=false --name=mom --count=40'
});

parser.addArgument(
  [ '-m', '--lunar-month' ],
  {
    help: 'birthday lunar month',
    required: true,
  }
);

parser.addArgument(
  [ '-d', '--lunar-day' ],
  {
    help: 'birthday lunar day',
    required: true,
  }
);

parser.addArgument(
  [ '-l', '--leap' ],
  {
    help: 'leap lunar date',
    defaultValue: false,
  }
);

parser.addArgument(
  [ '-n', '--name' ],
  {
    help: 'person name',
    required: true,
  }
);

parser.addArgument(
  [ '-c', '--count' ],
  {
    help: 'year counts',
    defaultValue: 10,
    required: true,
  }
);

module.exports = parser;